import { useEffect, useState } from "react"
import { useParams } from "react-router-dom";
// import { Link } from 'react-router-dom'

const Kanji = () => {
    const [kanji, setKanji] = useState(false)
    const { id } = useParams()
    const api = 'http://localhost:4000/api/kanji/' + id;

    useEffect(() => {
        const fetchKanji = async () => {
            const response = await fetch(api)
            const json = await response.json()

            if (response.ok) {
                setKanji(json)
            }
        }

        fetchKanji()
    }, [setKanji, id])

    return (
        <div className="Kanji">
            <div className="character">
                {kanji.character}
            </div>
            <div className="meaning">{kanji.meaning}</div>
            <div className="sub-heading">Onyomi</div>
            <div className="">{kanji.onyomi}</div>
            <div className="sub-heading">Kunyomi</div>
            <div className="">{kanji.kunyomi}</div>
        </div>
    )
}

export default Kanji
