import { useState, useRef, useContext } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faFloppyDisk } from '@fortawesome/free-solid-svg-icons';
import renderHTML from 'react-render-html'
import { updateMnemonic } from '../hooks/updateMnemonic';
import { UserContext } from '../App';

const Mnemonic = ({ mnemonic, kanjiId }) => {
    const [editing, setEditing] = useState(false)
    const [icon, setIcon] = useState(faEdit)
    const [iconClass, setIconClass] = useState("")
    const { user } = useContext(UserContext)
    const userId = user._id

    const mnemonicText = useRef(null)

    const toggleEditing = () => {
        if (!editing) {
            setIconClass("fa-beat-fade")
            setIcon(faFloppyDisk)
            mnemonicText.current.focus()
        }
        else {
            setIconClass("")
            setIcon(faEdit)
            mnemonicText.current.blur()
            updateMnemonic({ kanjiId, userId, mnemonicText })
        }

        setEditing(!editing)
    }

    return (
        <div className="mnemonic">
            <p>How to remember:</p>
            <div className="mnemonicText" contentEditable={editing} suppressContentEditableWarning={true} ref={mnemonicText} tabIndex={0}>
                {mnemonic && renderHTML(mnemonic.text)}
                <FontAwesomeIcon icon={icon} className={iconClass} onClick={() => toggleEditing()} />
            </div>
        </div>
    )
}

export default Mnemonic
