import { useState, useRef, useContext, useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faFloppyDisk } from '@fortawesome/free-solid-svg-icons';
import renderHTML from 'react-render-html'
import { updateKanji } from '../hooks/updateKanji';
import { KanjiContext, UserContext } from '../App';

const Mnemonic = ({ kanji }) => {
    const [allKanji, setAllKanji] = useContext(KanjiContext)
    const [editing, setEditing] = useState(false)
    const [icon, setIcon] = useState(faEdit)
    const [iconClass, setIconClass] = useState("")
    const [mnemonic, setmnemonic] = useState("")
    const { user } = useContext(UserContext)
    const userId = user._id
    const textDiv = useRef(null)

    // useEffect(() => {
    //     let mnemonicFound = kanji.mnemonic.find(x => x.user_id === userId)
    //     if (!mnemonicFound) {
    //         mnemonicFound = kanji.mnemonic.find(x => x.user_id.length === 0)
    //     }
    //     setmnemonic(mnemonicFound)
    // }, [kanji, userId, setmnemonic])

    // const toggleEditing = () => {
    //     if (!editing) {
    //         setIconClass("fa-beat-fade")
    //         setIcon(faFloppyDisk)
    //         textDiv.current.focus()
    //     }
    //     else {
    //         setIconClass("")
    //         setIcon(faEdit)
    //         textDiv.current.blur()
    //         const updatedText = textDiv.current.innerHTML.substring(0, textDiv.current.innerHTML.indexOf("<svg"))
    //         const updatedKanji = updateKanji({ kanji, userId, updatedText })

    //         const indexFound = allKanji.findIndex(x => x._id === updateKanji._id)
    //         allKanji[indexFound] = updatedKanji
    //         setAllKanji(allKanji)
    //     }

    //     setEditing(!editing)
    // }

    return (
        <div className="mnemonic">
            <p>How to remember:</p>
            <div className="textDiv" contentEditable={editing} suppressContentEditableWarning={true} ref={textDiv} tabIndex={0}>
                {mnemonic && renderHTML(mnemonic.text)}
                {/* <FontAwesomeIcon icon={icon} className={iconClass} onClick={() => toggleEditing()} /> */}
            </div>
        </div>
    )
}

export default Mnemonic
