const DEFAULT_TOOLTIP_ID = 'DEFAULT_TOOLTIP_ID'
const DEFAULT_CONTEXT_DATA: TooltipContextData = {
  anchorRefs: new Set(),
  activeAnchor: { current: null },
  attach: () => {
    /* attach anchor element */
  },
  detach: () => {
    /* detach anchor element */
  },
  setActiveAnchor: () => {
    /* set active anchor */
  },
}

