export const updateKanji = async ({ kanji, userId, updatedText }) => {
    const foundIndex = kanji.mnemonic.findIndex(x => x.user_id == userId)
    if (foundIndex) {
        kanji.mnemonic[foundIndex] = {
            text: updatedText,
            user_id: [userId]
        }
    }

    const response = await fetch('/api/kanji/' + kanji._id, {
        method: 'POST',
        body: JSON.stringify({ kanji }),
        headers: {
            'Content-Type': 'application/json'
        }
    })
    const json = await response.json()
    console.log(json)
}
