import { useEffect, useState, useContext } from "react"
import { useParams, useNavigate } from "react-router-dom";
import { KanjiContext } from "../App"
import { fetchKanji } from "../hooks/fetchKanji"
import KanjiDetails from '../components/KanjiDetails'
// import { Link } from 'react-router-dom'

const Kanji = () => {
    const { kanji, setKanji } = useContext(KanjiContext)
    const [selectedKanji, setSelectedKanji] = useState(false)
    const { id } = useParams()
    const navigate = useNavigate();

    // setSelectedKanji(kanji.find(kanji => kanji._id === id));
    // console.log(kanji.find(kanji => kanji._id === id))
    // const foundKanji = kanji.find(kanji => kanji._id === id);
    // setSelectedKanji(foundKanji)

    useEffect(() => {
        const fetchKanji = async () => {
            // const response = await fetch('/api/kanji')
            const response = await fetch('http://localhost:4000/api/kanji')
            const json = await response.json()

            if (response.ok) {
                setKanji(json)
            }
        }
        fetchKanji()
        console.log(kanji)
    }, [setKanji])


    function nextKanji() {
        console.log(kanji)
    }

    return (
        <div className="Kanji">
            <button className="backButton" onClick={() => navigate(-1)}>Back</button>
            <button className="nextButton" onClick={() => nextKanji()}>Next</button>
            <div className="character">{selectedKanji.character}</div>
            <KanjiDetails kanji={selectedKanji} />

            <div className="mnemonic">
                <p>How to remember:</p>
                <div className="mnemonicText">{selectedKanji.mnemonic}</div>
            </div>
        </div>
    )
}

export default Kanji
