import { createContext, useReducer } from 'react'

export const KanjiContext = createContext()

export const kanjiReducer = (state, action) => {
    switch (action.type) {
        case 'SET_KANJI':
            return {
                kanji: action.payload
            }
        // case 'CREATE_WORKOUT':
        //     return {
        //         workouts: [action.payload, ...state.workouts]
        //     }
        // case 'DELETE_WORKOUT':
        //     return {
        //         workouts: state.workouts.filter((w) => w._id !== action.payload._id)
        //     }
        default:
            return state
    }
}

export const KanjiContextProvider = ({ children }) => {
    const [state, dispatch] = useReducer(kanjiReducer, {
        kanji: null
    })

    return (
        <KanjiContext.Provider value={{ ...state, dispatch }}>
            {children}
        </KanjiContext.Provider>
    )
}
