import { useContext } from 'react'
import { UserContext } from "../App"

export const useLogout = () => {

    const { user, setUser } = useContext(UserContext)

    const logout = () => {
        // remove user from storage
        localStorage.removeItem('user')

        setUser(null)
    }

    return { logout }
}
