export const updateKanji = async ({ kanji }) => {
    const response = await fetch('/api/kanji/' + kanji._id, {
        method: 'POST',
        body: JSON.stringify({ kanji }),
        headers: {
            'Content-Type': 'application/json'
        }
    })
    const json = await response.json()
    console.log(json)
}
