import { KanjiContext } from "../context/KanjiContext";
import { useContext } from 'react'

export const useKanjiContext = () => {
    const context = useContext(KanjiContext)

    if (!context) {
        throw Error('useKanjiContext must be used inside a KanjiContextProvider')
    }

    return context
}
