import { useState, useRef } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faFloppyDisk } from '@fortawesome/free-solid-svg-icons';
import renderHTML from 'react-render-html'

const Mnemonic = ({ mnemonic }) => {
    const [editing, setEditing] = useState(false)
    const [icon, setIcon] = useState(faEdit)

    const mnemonicText = useRef(null)

    const editMnemonic = () => {
        setEditing(true)
        setIcon(faFloppyDisk)
        mnemonicText.current.focus()
    }

    return (
        <div className="mnemonic">
            <p>How to remember:</p>
            <div className="mnemonicText" contentEditable={editing} suppressContentEditableWarning={true} ref={mnemonicText}>
                {mnemonic && renderHTML(mnemonic.text)}
                <FontAwesomeIcon icon={icon} onClick={() => editMnemonic()} />
            </div>
        </div>
    )
}

export default Mnemonic
